package sampleTasks;

public class Q1_Size {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=12;
		char b='a';
		float c=34.22f;
		String d="hi hello";
		double e=456777;
		System.out.println(a+" "+Integer.SIZE);
		System.out.println(b+" "+Character.SIZE);
		System.out.println(c+" "+Float.SIZE);
		System.out.println(d+" "+d.length());
		System.out.println(e+" "+Double.SIZE);
	}

}
